#!/usr/bin/env node

const fs = require('fs');
const path = require('path');

function extractUrls(jsonFilePath) {
  try {
    // Read and parse the JSON file
    const jsonContent = fs.readFileSync(jsonFilePath, 'utf8');
    const data = JSON.parse(jsonContent);

    // Check if otherGroups exists and is an array
    if (!data.otherGroups || !Array.isArray(data.otherGroups)) {
      console.error('Error: JSON file must contain an "otherGroups" array');
      process.exit(1);
    }

    // Generate URLs from the otherGroups array
    const urls = data.otherGroups.map(group =>
      `https://www.debenhams.com/wordsworth/${group}?forceOfficeIp=true`
    );

    // Output each URL on a new line
    urls.forEach(url => console.log(url));

  } catch (error) {
    if (error.code === 'ENOENT') {
      console.error(`Error: File '${jsonFilePath}' not found`);
    } else if (error instanceof SyntaxError) {
      console.error(`Error: Invalid JSON in file '${jsonFilePath}'`);
    } else {
      console.error(`Error: ${error.message}`);
    }
    process.exit(1);
  }
}

// Get the file path from command line arguments
const jsonFilePath = process.argv[2];

if (!jsonFilePath) {
  console.error('Usage: node extract-urls.js <json-file-path>');
  process.exit(1);
}

extractUrls(jsonFilePath);

